import React, { Component } from "react";

class Filter extends Component {
  constructor(props) {
    super(props);
  }
  handleUserClick(option) {
    const l = this.props.products;
    const skus = Object.keys(this.props.products);
    const items = skus.map(sku => this.props.products[sku]);
    for (var i in items) {
      if (i.size.split().includes(option) == false) {
        delete l[i];
      }
    }
    this.props.applyFilter(l);
  }
  render() {
    return (
      <div>
        <button
          onClick={() => {
            this.handleUserClick("S");
          }}
        >
          S
        </button>
      </div>
    );
  }
}

export default Filter;
