import React, { Component } from "react";

class Filter extends Component {
  constructor(props) {
    super(props);
  }
  handleUserClick(option) {
    const l = new Array();
    const origin = new Array();
    const skus = Object.keys(this.props.products);

    skus.map(sku => {
      console.log(this.props.products[sku]);
      l.push(this.props.products[sku]);
      origin.push(this.props.products[sku]);
    });
    console.log(l);
    for (var i = 0; i < origin.length; i++) {
      console.log(l[i].size);
      var sizes = l[i].size.split();
      if (sizes.includes("XS")) {
        console.log("yes");
      }
    }
    this.props.applyFilter(l);
  }
  render() {
    return (
      <div>
        <button
          onClick={() => {
            this.handleUserClick("S");
          }}
        >
          S
        </button>
      </div>
    );
  }
}

export default Filter;
